import React, { useState } from 'react';
import { Container, Row, Col, Card, Form, Button, Alert, Spinner } from 'react-bootstrap';
import axios from 'axios';

const Login = ({ onLogin }) => {
  const [credentials, setCredentials] = useState({
    username: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setCredentials({
      ...credentials,
      [e.target.name]: e.target.value
    });
    // Clear error when user starts typing
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await axios.post('/api/auth.php', credentials);
      if (response.data.success) {
        onLogin(response.data.user);
      }
    } catch (error) {
      const errorMessage = error.response?.data?.error || 'Anmeldung fehlgeschlagen';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page bg-light min-vh-100">
      {/* Header */}
      <div className="header-kl">
        <Container>
          <div className="logo-container py-3">
            <div className="d-flex align-items-center">
              <div className="me-3">
                <svg width="50" height="50" viewBox="0 0 40 40" fill="currentColor">
                  <rect x="5" y="5" width="30" height="30" rx="3" fill="white"/>
                  <path d="M15 12h10v2H15v-2zm0 4h10v2H15v-2zm0 4h6v2h-6v-2z" fill="currentColor"/>
                  <circle cx="30" cy="30" r="8" fill="#10b981"/>
                  <path d="M26 30l2 2 6-6" stroke="white" strokeWidth="2" fill="none"/>
                </svg>
              </div>
              <div>
                <div className="logo-text">PDFSiegel</div>
                <div style={{fontSize: '1rem', opacity: 0.9}}>Sicher. Einfach. Signiert.</div>
              </div>
            </div>
          </div>
        </Container>
      </div>

      <Container className="py-5">
        <Row className="justify-content-center">
          <Col md={6} lg={4}>
            <Card className="shadow-sm">
              <Card.Body className="p-4">
                <div className="text-center mb-4">
                  <h1 className="h3 mb-2">Anmelden</h1>
                  <p className="text-muted">
                    Melden Sie sich mit Ihren Active Directory Zugangsdaten an
                  </p>
                </div>

                {error && (
                  <Alert variant="danger" className="alert-kl-danger" role="alert">
                    <strong>Fehler:</strong> {error}
                  </Alert>
                )}

                <Form onSubmit={handleSubmit} noValidate>
                  <Form.Group className="mb-3">
                    <Form.Label htmlFor="username" className="required">
                      AD Nummer
                    </Form.Label>
                    <Form.Control
                      id="username"
                      type="text"
                      name="username"
                      value={credentials.username}
                      onChange={handleChange}
                      placeholder="AD Nummer eingeben"
                      required
                      disabled={loading}
                      aria-describedby="username-help"
                      autoComplete="username"
                    />
                    <Form.Text id="username-help" className="text-muted">
                      Verwenden Sie Ihre Active Directory Nummer
                    </Form.Text>
                  </Form.Group>

                  <Form.Group className="mb-4">
                    <Form.Label htmlFor="password" className="required">
                      Passwort
                    </Form.Label>
                    <Form.Control
                      id="password"
                      type="password"
                      name="password"
                      value={credentials.password}
                      onChange={handleChange}
                      placeholder="Passwort eingeben"
                      required
                      disabled={loading}
                      autoComplete="current-password"
                    />
                  </Form.Group>

                  <Button
                    type="submit"
                    className="btn-kl-primary w-100"
                    size="lg"
                    disabled={loading || !credentials.username || !credentials.password}
                  >
                    {loading ? (
                      <>
                        <Spinner
                          as="span"
                          animation="border"
                          size="sm"
                          role="status"
                          aria-hidden="true"
                          className="me-2"
                        />
                        <span className="sr-only">Laden...</span>
                        Anmelden...
                      </>
                    ) : (
                      'Anmelden'
                    )}
                  </Button>
                </Form>

                {/* Demo-Hinweis für Entwicklung */}
                <div className="mt-4 p-3 bg-light rounded">
                  <small className="text-muted">
                    <strong>Demo-Zugangsdaten:</strong><br />
                    Benutzername: admin, mchairman<br />
                    Passwort: password (für alle Demo-Benutzer)
                  </small>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Login;
